import { Box, Image, Text, Badge, Flex, IconButton, Skeleton } from '@chakra-ui/react'
import {BiExpand} from 'react-icons/bi';
import React from 'react'

const ProductCard = ({product, loading}) => {
  return (
   <Skeleton isLoaded={!loading} _hover={{ size:1.5 }}>
        <Box _hover={{transform:'scale(1.1)', transitionDuration: '0.5s'}}
        borderWidth='1px'
        overflow='hidden'
        p='4'
        shadow='md'
        > 
            <Image src={product.image[0]} fallbackSrc='https://via.placeholder.com/150' alt={product.name}/>
            {product.stock < 5 ? (
                <Badge colorScheme='yellow'> only {product.stock} left</Badge>
            ): product.stock < 1? (
                <Badge colorScheme='red'> Sold Out </Badge>
            ): <Badge colorScheme='green'> In Stock</Badge>}
            {product.productIsNew && (<Badge ml={'2'} colorScheme='purple'> New </Badge>)}
            <Text noOfLines={1} fontSize={'xl'} fontWeight={'semibold'} marginTop={'2'}>
                {product.brand} {' '} {product.name}
            </Text>
            <Text noOfLines={1} fontSize={'md'} color={'gray.600'}>
                {product.subtitle}
            </Text>
            <Flex justify={'space-between'} alignItems={'center'} marginTop={'2'}>
            <Badge colorScheme='cyan'> {product.category}</Badge>
            <Text fontSize={'xl'} fontWeight={'semibold'} color={'cyan.600'}>
                ${product.price}
            </Text>
            </Flex>
            <IconButton icon={<BiExpand size='20'/>} colorScheme='cyan' size={'sm'} />
        </Box>
   </Skeleton>
  )
}

export default ProductCard
